import React, { useEffect, useRef } from 'react';
import '../../App.css';
import Cards from '../Cards';
import Footer from '../Footer';

function Home() {

    const containerRef = useRef(null);

    useEffect(() => {
        const container = containerRef.current;
        const hoverArea = container.querySelector('.hover-area');

        const handleMouseMove = (event) => {
            const mouseX = event.clientX - container.offsetLeft;
            const mouseY = event.clientY - container.offsetTop;

            hoverArea.style.transform = `translate(${mouseX}px, ${mouseY}px)`;
        };

        container.addEventListener('mousemove', handleMouseMove);

        return () => {
            container.removeEventListener('mousemove', handleMouseMove);
        };
    }, []);


    return (
        <>
            <div className="content">
                <div className="home">
                    <h1 className="home-heading">HOME</h1>
                    <p className="home-text">
                        <br/>
                        Welcome to My Creative Playground: Exploring the Intersection of Art, Games, and Web Design!
                    </p>
                </div>
            </div>
            <div className="container" ref={containerRef}>
                <div className="hover-area" ref={hoverAreaRef}></div>
                <div className="text-description">
                    <h2>Hover Effect Title</h2>
                    <p>This is the description that will be revealed on hover.</p>
                </div>
            </div>
            <Cards/>
            <Footer/>
        </>
    );
}

    export default Home;
