import React, { useEffect, useRef, useState } from 'react';
import '../../App.css';
import Cards from '../Cards';
import Footer from '../Footer';

function Home() {
    const containerRef = useRef(null);
    const hoverAreaRef = useRef(null);
    const [flashlightOn, setFlashlightOn] = useState(true);
    const [mouseX, setMouseX] = useState(null);
    const [mouseY, setMouseY] = useState(null);
    const [flashlightPosition, setFlashlightPosition] = useState(null);

    useEffect(() => {
        const container = containerRef.current;
        const hoverArea = hoverAreaRef.current;

        const handleMouseMove = (event) => {
            if (!flashlightOn) return;

            const containerRect = container.getBoundingClientRect();
            const mouseX = event.clientX - containerRect.left;
            const mouseY = event.clientY - containerRect.top;

            setMouseX(mouseX);
            setMouseY(mouseY);

            if (flashlightOn) {
                setFlashlightPosition({ x: mouseX, y: mouseY });
            }
        };

        container.addEventListener('mousemove', handleMouseMove);

        return () => {
            container.removeEventListener('mousemove', handleMouseMove);
        };
    }, [flashlightOn]);

    const handleClick = () => {
        if (!flashlightOn) {
            setFlashlightPosition({ x: mouseX, y: mouseY });
        } else {
            setFlashlightPosition(null);
        }

        setFlashlightOn(!flashlightOn);
    };

    return (
        <>
            <div
                className={`container ${flashlightOn ? '' : 'flashlight-off'}`}
                ref={containerRef}
                onClick={handleClick}
            >
                <div
                    className={`hover-area ${flashlightOn ? '' : 'hidden'}`}
                    ref={hoverAreaRef}
                    style={{ transform: `translate(${flashlightPosition?.x}px, ${flashlightPosition?.y}px)` }}
                ></div>
                <div className="text-description">
                    <h1 className="home-heading">HOME</h1>
                    <p className="home-text">
                        <br />
                        Welcome to My Creative Playground: Exploring the Intersection of Art, Games, and Web Design!
                    </p>
                </div>
            </div>
            <Cards />
            <Footer />
        </>
    );
}

export default Home;
