import React, { useState } from 'react';
import '../../App.css';
import Cards from '../Cards';
import Footer from '../Footer';

function Home() {
    const [flashlightOn, setFlashlightOn] = useState(true);
    const [flashlightPosition, setFlashlightPosition] = useState({ x: null, y: null });

    const handleContainerClick = (event) => {
        const containerRect = event.target.getBoundingClientRect();
        const mouseX = event.clientX - containerRect.left;
        const mouseY = event.clientY - containerRect.top;

        setFlashlightOn(!flashlightOn);
        setFlashlightPosition(flashlightOn ? { x: null, y: null } : { x: mouseX, y: mouseY });
    };

    return (
        <>
            <div className={`container ${flashlightOn ? '' : 'flashlight-off'}`} onClick={handleContainerClick}>
                {flashlightOn && (
                    <div
                        className="hover-area"
                        style={{ transform: `translate(${flashlightPosition.x}px, ${flashlightPosition.y}px)` }}
                    ></div>
                )}
                <div className="text-description">
                    <h1 className="home-heading">HOME</h1>
                    <p className="home-text">
                        <br />
                        Welcome to My Creative Playground: Exploring the Intersection of Art, Games, and Web Design!
                    </p>
                </div>
            </div>
            <Cards />
            <Footer />
        </>
    );
}

export default Home;
