import {useState, useEffect} from 'react';
import {Link} from 'react-router-dom';
import './Navbar.css';
import {Button} from "./Button";
import { NavLink } from 'react-router-dom';

function Navbar() {
    const [click, setClick] = useState(false);
    const [button, setButton] = useState(true);

    const handleClick = () => setClick(!click);
    const closeMobileMenu = () => setClick(false);

    const showButton = () => {
        if(window.innerWidth <= 930) {
            setButton(false)
            if (click) {
                setClick(false);
            }
        } else {
            setButton(true);
        }
    };

    useEffect(() => {
        showButton();
    }, []);

    useEffect(() => {
        const smoothScroll = (targetId) => {
            const targetElement = document.getElementById(targetId);
            if (targetElement) {
                const offset = 8 * parseFloat(getComputedStyle(document.documentElement).fontSize); // 8rem in pixels
                window.scrollTo({
                    top: targetElement.offsetTop - offset,
                    behavior: 'smooth',
                });
            }
        };

        // Add smooth scroll behavior when clicking on anchor links
        document.querySelectorAll('.nav-links').forEach((link) => {
            link.addEventListener('click', (e) => {
                e.preventDefault();
                const targetId = e.target.getAttribute('href').substring(1); // Get the target ID without the '#'
                smoothScroll(targetId);
            });
        });
    }, []);

    const [emoji, setEmoji] = useState("ðŸŒ•");

    window.addEventListener('resize', showButton);

    const toggleDarkMode = () => {
        const root = document.documentElement;
        const isDarkMode = root.classList.contains('dark-mode');
        if (isDarkMode) {
            root.style.setProperty('--background', '#ffffff');
            root.style.setProperty('--text', '#222628');
            root.style.setProperty('--card', '#e6e7ff');
            root.style.setProperty('--heading', '#32374b');
            //root.style.setProperty('--nav-background', '#959CF8');
        } else {
            root.style.setProperty('--background', '#222628'); //#32324b
            root.style.setProperty('--text', '#D3D3F0');
            root.style.setProperty('--card', '#32374b');
            root.style.setProperty('--heading', '#959CF8');
            //root.style.setProperty('--nav-background', '#656FC8');
        }
        setEmoji(isDarkMode ? "ðŸŒ•" : "ðŸŒ‘");
        root.classList.toggle('dark-mode');
    };

    const [scrollingElement, setScrollingElement] = useState(null);
    const [activeSection, setActiveSection] = useState('home');

    useEffect(() => {
        const observer = new IntersectionObserver(
            (entries) => {
                entries.forEach((entry) => {
                    if (entry.isIntersecting) {
                        setActiveSection(entry.target.id);
                    }
                });
            },
            { threshold: 0.5 } // Intersection threshold set to 50%
        );

        // Observe sections you want to highlight in the navbar
        observer.observe(document.getElementById('home'));
        observer.observe(document.getElementById('game'));
        observer.observe(document.getElementById('web'));
        observer.observe(document.getElementById('art'));

        // Clean up the observer on component unmount
        return () => observer.disconnect();
    }, []); // Empty dependency array ensures that this effect runs once after the initial render

    useEffect(() => {
        // Set the scrolling element for smooth scroll
        setScrollingElement(document.documentElement);

        // Clean up the scrolling element on component unmount
        return () => {
            setScrollingElement(null);
        };
    }, []);

    return (
        <>
            <nav className="navbar">
                <div className="navbar-container">
                    <Link
                        to="home"
                        smooth={true}
                        duration={500}
                        spy={true}
                        offset={-80}
                        className={`nav-links ${activeSection === 'home' ? 'active' : ''}`}
                    >
                        Home
                    </Link>
                    <Link
                        to="game"
                        smooth={true}
                        duration={500}
                        spy={true}
                        offset={-80}
                        className={`nav-links ${activeSection === 'game' ? 'active' : ''}`}
                    >
                        Game Dev
                    </Link>
                    <Link
                        to="web"
                        smooth={true}
                        duration={500}
                        spy={true}
                        offset={-80}
                        className={`nav-links ${activeSection === 'web' ? 'active' : ''}`}
                    >
                        Web Dev
                    </Link>
                    <Link
                        to="art"
                        smooth={true}
                        duration={500}
                        spy={true}
                        offset={-80}
                        className={`nav-links ${activeSection === 'art' ? 'active' : ''}`}
                    >
                        Artwork
                    </Link>
                    <button onClick={toggleDarkMode}>{emoji}</button>
                </div>
            </nav>



        </>
    )
}

export default Navbar;
