import React from 'react';
import '../../App.css';
import Cards from '../Cards';
import HeroSection from '../HeroSection';
import Footer from '../Footer';

function Home() {

    const container = document.querySelector('.container');
    const hoverArea = document.querySelector('.hover-area');

    container.addEventListener('mousemove', function(event) {
        const mouseX = event.clientX - container.offsetLeft;
        const mouseY = event.clientY - container.offsetTop;

        hoverArea.style.transform = `translate(${mouseX}px, ${mouseY}px)`;
    });


    return (
        <>
            <div className="content">
                <div className="home">
                    <h1 className="home-heading">HOME</h1>
                    <p className="home-text"><br></br>Welcome to My Creative Playground:
                        Exploring the Intersection of Art, Games, and Web Design!</p>
                </div>
            </div>
            <div className="container">
                <div className="hover-area"></div>
                <div className="text-description">
                    <h2>Hover Effect Title</h2>
                    <p>This is the description that will be revealed on hover.</p>
                </div>
            </div>
                <Cards />
                <Footer />

        </>
    );
}

export default Home;
